sys_variable_value.value:
(function execute(inputs, outputs) {

  if (inputs.code==200) {
    var responseObject = JSON.parse(inputs.response);
	var responseLength=0;

    var wikiLog = new GlideRecord('x_snc_vapedia_wikipedia_search_log');
    wikiLog.initialize();
    wikiLog.query=inputs.query;
    wikiLog.insert();

    for (i=0; i<10; i++) {
      if (responseObject["query"]["search"][i]["title"].toString()!='') {

        var wikiPage = new GlideRecord('x_snc_vapedia_wikipage');
        wikiPage.initialize();
        wikiPage.pageid = responseObject["query"]["search"][i]["pageid"].toString();
        wikiPage.title = responseObject["query"]["search"][i]["title"].toString();
        wikiPage.url = 'https://en.wikipedia.org/?curid='+responseObject["query"]["search"][i]["pageid"].toString();
        wikiPage.snippet = responseObject["query"]["search"][i]["snippet"].toString();
        wikiPage.order = i;
        wikiPage.query=wikiLog.getUniqueValue();
        wikiPage.insert();

        responseLength++;

    }
  }
    outputs.query_sysid=wikiLog.getUniqueValue();
    outputs.responselength=responseLength;
  }

})(inputs, outputs);

